import React from 'react'
import { Redirect, useParams } from 'react-router-dom'
import { getHeroesById } from '../../selectors/getHeroesById'



export const HeroesScreen = () => {

    const {heroesId} = useParams()// extrae todo los valores que vienen por url 

    const heroe = getHeroesById(heroesId)

    if(!heroe) {
        return <Redirect to="/" />
    }

    const {

        superhero, 
        alter_ego,
        first_appearance,
        characters
    } = heroe;

    return (

        <div className="row mt-5">

            <div className="col-4">
                <img 
                className="img-thumbnail"
                src={`../assets/heroes/${heroesId}.jpg`} 
                alt = {superhero}
                />

            </div>
            <div className='col-8'>
                <h2 style = {{ textAlign:'center'}}> {superhero} </h2>
                <ul className='list-group list-group-flush'>
                    <li className='list-group-item'><b>Alter Ego:</b> {alter_ego}</li>
                    <li className='list-group-item'><b>first_appearance:</b> {alter_ego}</li>



                </ul>


            </div>



        </div>
    )
}
