import React, { useState } from 'react'
import { heroes } from '../../data/heroes'
import { HeroeCard } from '../heroes/HeroeCard'
import { useFormset } from '../hooks/useForm'




export const SearchScreen = () => {


    const [values, handleInput,reset] = useFormset({
        searchText:''

    })

    const handleSearch = (e) => {
        e.preventDefault()

        heroesValues = heroes.filter(heroe => heroe.superhero ==e.target.value)
        console.log(e.target.input)

    }

    return (
        <div>
            <h1>Search</h1> 
            <hr/>
            <div className="row">
                <div className="col-5">
            <form
            onSubmit = {handleSearch}
            >

                <input 
                className="form-control"
                type="text"
                placeholder="Search Heroe" 
                onSubmit = {handleSearch}
                name = 'searchText'
                value = {searchText}
                />
                <button 

                type="submit"
                className="btn m-1 btn-block btn-outline-primary">
                    Search..
                </button>



            </form>


                </div>
                <div className="col-7">
                    <h3>Result</h3>
                    <hr/>
                    {
                        heroesValues.map(heroe=> (
                            <HeroeCard
                            key = {heroe.id}
                            {...heroe}
                            />
                            )
                            )

                    }






                </div>




            </div>



        </div>
    )
}
